mapper.select:
  		select * from t_movie
  		
  			
  				name like concat('%',#{name},'%')
  			
  			 
  			 and actor like concat('%',#{actor},'%')
  			
  			
  				and years=#{years}
  			
  			
  			 and uptime >=#{u1}
  			
  			
  			 and uptime <=#{u1}
  			
  			
  			 and price >=#{p1}
  			
  			
  			 and price <=#{p2}
  			
  			
  			 and longtime >=#{l1}
  			
  			
  			 and longtime <=#{l2}
  			 
  		
  		
		
		  order by  ${orderName} ${orderMethod}
